import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './OrderManagement.css';

interface Order {
  id: number;
  customerName: string;
  orderId: string;
  date: string;
  estimatedDeliveryDate: string;
}

const OrderManagement: React.FC = () => {
  const navigate = useNavigate();
  const [orders, setOrders] = useState<Order[]>([
    {
      id: 1,
      customerName: 'John Doe',
      orderId: '#ORD1023',
      date: '2025-04-15',
      estimatedDeliveryDate: '2025-04-20'
    },
    {
      id: 2,
      customerName: 'Jane Smith',
      orderId: '#ORD1024',
      date: '2025-04-16',
      estimatedDeliveryDate: '2025-04-22'
    }
  ]);

  const handleSignOut = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/signin');
  };

  const handleEdit = (id: number, field: keyof Order, value: string) => {
    setOrders(orders.map(order => 
      order.id === id ? { ...order, [field]: value } : order
    ));
  };

  return (
    <div className="order-management">
      <header>
        <nav>
          <a href="/">Home</a>
          <a href="/gallery">Gallery</a>
          <a href="/orders">Orders</a>
          <a href="/contact">Contact</a>
        </nav>
        <button className="sign-out-btn" onClick={handleSignOut}>
          Sign Out
        </button>
      </header>

      <h2>Order Management</h2>

      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th>Serial Number</th>
              <th>Customer Name</th>
              <th>Order ID</th>
              <th>Date</th>
              <th>Estimated Delivery Date</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order) => (
              <tr key={order.id}>
                <td>{order.id}</td>
                <td
                  contentEditable
                  onBlur={(e) => handleEdit(order.id, 'customerName', e.currentTarget.textContent || '')}
                >
                  {order.customerName}
                </td>
                <td
                  contentEditable
                  onBlur={(e) => handleEdit(order.id, 'orderId', e.currentTarget.textContent || '')}
                >
                  {order.orderId}
                </td>
                <td
                  contentEditable
                  onBlur={(e) => handleEdit(order.id, 'date', e.currentTarget.textContent || '')}
                >
                  {order.date}
                </td>
                <td
                  contentEditable
                  onBlur={(e) => handleEdit(order.id, 'estimatedDeliveryDate', e.currentTarget.textContent || '')}
                >
                  {order.estimatedDeliveryDate}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default OrderManagement;
